html {
  --bg: #161d26;
  --bg-color: #1f2935;
  --color: #95a5c6;
  --white: #ffffff;
}

html[data-theme=dark] {
  --bg: #f5f5f5;
  --bg-color: #ffffff;
  --color: #1f2935;
  --white: #161d26;
}
html[data-theme=dark] .image {
  filter: invert(0.5);
}

*,
*::before,
*::after {
  box-sizing: border-box;
}

a {
  text-decoration: none;
}

body {
  margin: 0;
  padding: 0;
  font-family: sans-serif;
  background-color: var(--bg);
  overflow-x: hidden;
  scroll-behavior: smooth;
  scroll-snap-type: mandatory;
}

body::-webkit-scrollbar {
  background-color: var(--bg-color);
  border-radius: 12px;
}

body::-webkit-scrollbar-thumb {
  background-color: #0abead;
  border-radius: 12px;
}

.header {
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
  width: 100vw;
  position: fixed;
  top: 0;
  background-color: var(--bg-color);
  box-shadow: 0px 3px 10px rgba(0, 0, 0, 0.2);
  z-index: 1;
}

.nav {
  width: 100%;
  padding: 0em 2em;
  max-width: 1360px;
  height: 70px;
  display: flex;
  justify-content: space-between;
  align-items: center;
}

input[type=checkbox] {
  height: 0;
  width: 0;
  visibility: hidden;
}

label {
  cursor: pointer;
  text-indent: -9999px;
  width: 52px;
  height: 23px;
  background: #0abead;
  float: right;
  border-radius: 100px;
  position: relative;
}

label:after {
  content: "";
  position: absolute;
  top: 3px;
  left: 3px;
  width: 18px;
  height: 17px;
  background: var(--bg-color);
  border-radius: 90px;
  transition: 0.3s;
}

input:checked + label {
  background: #0abead;
}

input:checked + label:after {
  left: calc(100% - 5px);
  transform: translateX(-100%);
}

label:active:after {
  width: 45px;
}

html.transition,
html.transition *,
html.transition *:before,
html.transition *:after {
  transition: all 750ms !important;
  transition-delay: 0 !important;
}

.brand-name {
  font-size: 25px;
  color: #0abead;
  letter-spacing: 2px;
  text-decoration: none;
}

.menu-btn {
  margin-right: 20px;
  position: relative;
  display: none;
  justify-content: center;
  align-items: center;
  width: 30px;
  height: 25px;
  outline: none;
  cursor: pointer;
  transition: all 0.5s ease-in-out;
  /* border: 3px solid #fff; */
}

.menu-btn__burger {
  width: 25px;
  height: 3px;
  background: #0abead;
  border-radius: 5px;
  box-shadow: 0 2px 5px rgba(0, 0, 0, 0.288);
  transition: all 0.5s ease-in-out;
}

.menu-btn__burger::before,
.menu-btn__burger::after {
  content: "";
  position: absolute;
  width: 30px;
  height: 3px;
  background: #0abead;
  border-radius: 5px;
  box-shadow: 0 2px 5px rgba(0, 0, 0, 0.288);
  transition: all 0.5s ease-in-out;
}

.menu-btn__burger::before {
  transform: translateY(-10px);
}

.menu-btn__burger::after {
  transform: translateY(10px);
}

/* ANIMATION */
.menu-btn.open .menu-btn__burger {
  transform: translateX(-50px);
  background: transparent;
  box-shadow: none;
}

.menu-btn.open .menu-btn__burger::before {
  transform: rotate(45deg) translate(35px, -35px);
}

.menu-btn.open .menu-btn__burger::after {
  transform: rotate(-45deg) translate(35px, 35px);
}

